sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//Define the getEmail function
	//Create a GlideRecord for the User table
	//Use the sysparm_userId parameter to get the correct user
	//Return the email of that user
    getEmail:function(){
		var userRec=new GlideRecord("sys_user");
		userRec.get(this.getParameter('sysparm_userId'));
		var myObj={};
		myObj.emailUsr=userRec.email+'';
		myObj.emailBck=userRec.u_backup.email+'';
		return JSON.stringify(myObj);
	},
    type: 'GetEmailAddress'
});
